          op : in ,
          content :{  
             field : case.project.project_id ,
             value :[  
                TCGA-DLBC 
            ]
         }
      },
      {  
          op : in ,
          content :{  
             field : case.ssm.consequence.transcript.annotation.impact ,
             value :[  
                HIGH ,
                MODERATE 
            ]
         }
      }
   ]
} 





